mapper.select:
SELECT*
FROM enterprise
WHERE
user_name= #{arg0}
AND
user_password= #{arg1}

SELECT count(*)
FROM enterprise
WHERE 1=1

and name = #{name,jdbcType=VARCHAR}


	and user_name = #{userName,jdbcType=VARCHAR}


and 	user_password = #{userPassword,jdbcType=VARCHAR}


	and email = #{email,jdbcType=VARCHAR}


and	address = #{address,jdbcType=VARCHAR}


and	linkman = #{linkman,jdbcType=VARCHAR}


and	phone = #{phone,jdbcType=VARCHAR}


and	industry_id = #{industryId,jdbcType=INTEGER}


and	zone_id = #{zoneId,jdbcType=INTEGER}


	and state = #{state,jdbcType=TINYINT}


and	image_log = #{imageLog,jdbcType=VARCHAR}


	and image_license = #{imageLicense,jdbcType=VARCHAR}


	and description = #{description,jdbcType=VARCHAR}


	and stage = #{stage,jdbcType=TINYINT}


	and create_time = #{createTime,jdbcType=TIMESTAMP}


and	update_time = #{updateTime,jdbcType=TIMESTAMP}

SELECT *
FROM enterprise
WHERE 1=1

	and name = #{name,jdbcType=VARCHAR}


and	user_name = #{userName,jdbcType=VARCHAR}


	and user_password = #{userPassword,jdbcType=VARCHAR}


	and email = #{email,jdbcType=VARCHAR}


	and address = #{address,jdbcType=VARCHAR}


	and linkman = #{linkman,jdbcType=VARCHAR}


and 	phone = #{phone,jdbcType=VARCHAR}


	and industry_id = #{industryId,jdbcType=INTEGER}


	and zone_id = #{zoneId,jdbcType=INTEGER}


and 	state = #{state,jdbcType=TINYINT}


	and image_log = #{imageLog,jdbcType=VARCHAR},


	and image_license = #{imageLicense,jdbcType=VARCHAR}


and	description = #{description,jdbcType=VARCHAR}


	and stage = #{stage,jdbcType=TINYINT}


and 	create_time = #{createTime,jdbcType=TIMESTAMP}


	and update_time = #{updateTime,jdbcType=TIMESTAMP}


	limit #{start},#{pageSize}
