mapper.select:
select *
from job
WHERE 1=1

	and enterprise_id = #{enterpriseId,jdbcType=INTEGER}


	and title = #{title,jdbcType=VARCHAR}


	and nature = #{nature,jdbcType=VARCHAR}


	and industry_id = #{industryId,jdbcType=INTEGER}


	and zone_id = #{zoneId,jdbcType=INTEGER}


	and address = #{address,jdbcType=VARCHAR}


	and effective_time = #{effectiveTime,jdbcType=TIMESTAMP}


	and expiration_time = #{expirationime,jdbcType=TIMESTAMP}


	and wage = #{wage,jdbcType=INTEGER}


	and peopleNumber = #{peopleNumber,jdbcType=INTEGER}


	and education = #{education,jdbcType=VARCHAR}


	and service_year = #{serviceYear,jdbcType=VARCHAR}


	and description = #{address,jdbcType=VARCHAR}


	and welfare = #{welfare,jdbcType=VARCHAR}


	and email = #{email,jdbcType=VARCHAR}
