mapper.select:
select *
from major
WHERE 1=1

	and code = #{code,jdbcType=CHAR}


	and classify_1 = #{classify1,jdbcType=VARCHAR}



	and classify_2 = #{classify2,jdbcType=VARCHAR}


	and name = #{name,jdbcType=VARCHAR}


	and institute = #{institute,jdbcType=VARCHAR}


	and school_id = #{schoolId,jdbcType=INTEGER}
